import React from "react";
import AppBar from "@mui/material/AppBar";
import ToolBar from "@mui/material/Toolbar";
import useScrollTrigger from "@mui/material/useScrollTrigger";
import { makeStyles } from "@mui/styles";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { Button, ListItem } from "@mui/material";
import { useState, useEffect } from "react";
import logo from "../../assets/logo.svg";
import { Link } from "react-router-dom";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import useMediaQuery from "@mui/material/useMediaQuery";
import { useTheme } from "@mui/material/styles";

import SwipeableDrawer from "@mui/material/SwipeableDrawer";
import IconButton from "@mui/material/IconButton";
import MenuIcon from "@mui/icons-material/Menu";

import List from "@mui/material/List";
// import { ListItem } from "@mui/material";
// import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
// import { RotateLeft, RouteSharp } from "@mui/icons-material";

function ElevationScroll(props) {
  const { children } = props;

  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
  });

  return React.cloneElement(children, {
    elevation: trigger ? 4 : 0,
  });
}

const useStyles = makeStyles((theme) => ({
  toolbarMargin: {
    ...theme.mixins.toolbar,
    marginBottom: "3em",
    [theme.breakpoints.down("md")]: {
      marginBottom: "2em",
    },
    [theme.breakpoints.down("xs")]: {
      marginBottom: "1.25em",
    },
  },
  logo: {
    height: "8em",
    [theme.breakpoints.down("md")]: {
      height: "7em",
    },
    [theme.breakpoints.down("xs")]: {
      height: "5.5em",
    },
  },
  logocontainer: {
    padding: "0 !important",
    "&:hover": {
      backgroundColor: "transparent",
    },
  },
  tabContainer: {
    marginLeft: "auto",
  },
  tab: {
    ...theme.typography.tab,
    minWidth: 10,
    marginLeft: "25px",
    color: "white !important",
  },
  button: {
    ...theme.typography.estimate,
    borderRadius: "50px !important",
    marginLeft: "50px !important",
    marginRight: "25px !important",
    height: "45px !important",
  },
  menu: {
    backgroundColor: theme.palette.common.blue,
    color: "white !important",
    borderRadius: "0px",
  },
  menuItem: {
    ...theme.typography.tab,
    opacity: 0.7,
    "&hover": {
      opacity: 1,
    },
  },
  drawerIcon: {
    height: "50px",
    width: "50px",
  },
  drawerIconContainer: {
    marginLeft: "auto !important",
    "&:hover": {
      backgroundColor: "transparent",
    },
  },
  drawer: {
    backgroundColor: theme.palette.common.blue,
  },
  drawerItem: {
    ...theme.typography.tab,
    color: "white",
    opacity: 0.7,
  },
  drawerItemEstimate: {
    backgroundColor: theme.palette.common.orange,
  },
  drawerItemselected: {
    opacity: 1,
  },
}));

function Header(props) {
  const classes = useStyles();
  const theme = useTheme();
  const iOS = typeof navigator !== "undefined" && /iPad|iPhone|iPod/.test(navigator.userAgent);

  const matches = useMediaQuery(theme.breakpoints.down("md"));

  const [openDrawer, setOpenDrawer] = useState(false);
  const [value, setValue] = useState(0);
  const [anchorEl, setAnchorEL] = useState(null);
  const [openMenu, setOpenMenu] = useState(false);
  // const [open, setOpen] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(0);

  const changeHandler = (e, newValue) => {
    setValue(newValue);
  };

  const clickHandler = (e) => {
    setAnchorEL(e.currentTarget);
    setOpenMenu(true);
  };
  const handeleMenuItemClick = (e, i) => {
    setAnchorEL(null);
    setOpenMenu(null);
    setSelectedIndex(i);
  };

  const handleClose = () => {
    setAnchorEL(null);
    setOpenMenu(false);
  };

  const menuOptions = [
    { id: 0, name: "Services", link: "/services", activeIndex: 1, selectedIndex: 0 },
    { id: 1, name: "Custom SoftWare Development", link: "/customsoftware", activeIndex: 1, selectedIndex: 1 },
    { id: 2, name: "Mobile Apps Development", link: "/mobileapps", activeIndex: 1, selectedIndex: 2 },
    { id: 3, name: "Websites", link: "/websites", activeIndex: 1, selectedIndex: 3 },
  ];
  const routes = [
    { id: 0, name: "Services", link: "/services", activeIndex: 1, selectedIndex: 0 },
    { id: 1, name: "Custom SoftWare Development", link: "/customsoftware", activeIndex: 1, selectedIndex: 1 },
    { id: 2, name: "Mobile Apps Development", link: "/mobileapps", activeIndex: 1, selectedIndex: 2 },
    { id: 3, name: "Websites", link: "/websites", activeIndex: 1, selectedIndex: 3 },
  ];
  useEffect(() => {
    [...menuOptions, ...routes].forEach((route) => {
      switch (window.location.pathname) {
        case `${route.link}`:
          if (value !== route.activeIndex) {
            setValue(route.activeIndex);
            if (route.selectedIndex && route.selectedIndex !== selectedIndex) {
              setSelectedIndex(route.selectedIndex);
            }
          }
          break;
        case "/estimate":
          setValue(5);
          break;
        default:
          break;
      }
    });
  }, [value, menuOptions, selectedIndex, routes]);

  const tabs = (
    <>
      <Tabs className={classes.tabContainer} value={value} onChange={changeHandler} indicatorColor="secondary">
        {routes.map((route) => (
          <Tab
            key={route.id}
            className={classes.tab}
            component={Link}
            to={route.link}
            lable={route.name}
            aria-owns={route.ariaOwse}
            aria-haspopup={route.ariaPopup}
            onMouseOver={route.mouseOver}
          />
        ))}
      </Tabs>
      <Button variant="contained" color="secondary" className={classes.button}>
        Free Estimate
      </Button>
      <Menu
        keepMounted
        id="basic-menu"
        anchorEl={anchorEl}
        open={openMenu}
        onClose={handleClose}
        MenuListProps={{
          "aria-labelledby": "basic-button",
        }}
        classes={{ paper: classes.menu }}
        elevation={0}
      >
        {menuOptions.map((option, i) => (
          <MenuItem
            key={option.id}
            onClick={(event) => {
              handeleMenuItemClick(event, i);
              setValue(1);
              handleClose();
            }}
            selected={i === selectedIndex && value === 1}
            component={Link}
            to={option.link}
            className={{ root: classes.menuItem }}
          >
            {option.name}
          </MenuItem>
        ))}
      </Menu>
    </>
  );

  const drawer = (
    <React.Fragment>
      <SwipeableDrawer
        disableBackdropTransition={!iOS}
        disableDiscovery={iOS}
        open={openDrawer}
        onClose={() => setOpenDrawer(false)}
        onOpen={() => setOpenDrawer(true)}
        Example
        Drawer
        classes={{ paper: classes.drawer }}
      >
        <List disablePadding>
          {routes.map((route) => (
            <ListItem
              key={route.id}
              divider
              button
              component={Link}
              to={route.link}
              selected={value === route.activeIndex}
              onClick={() => {
                setOpenDrawer(false);
                setValue(route.activeIndex);
              }}
            >
              <ListItemText
                // className={value === 0 ? [classes.drawerItem, classes.drawerItemselected] : classes.drawerItem}
                disableTypography
              >
                {route.name}
              </ListItemText>
            </ListItem>
          ))}

          <ListItem
            className={classes.drawerItemEstimate}
            onclick={() => {
              setOpenDrawer(false);
              setValue(5);
            }}
            divider
            button
            components={Link}
            to="/estimate"
            selected={value === 5}
          >
            <ListItemText
              className={value === 5 ? [classes.drawerItem, classes.drawerItemselected] : classes.drawerItem}
              disableTypography
            >
              Free Estimate
            </ListItemText>
          </ListItem>
        </List>
      </SwipeableDrawer>

      <IconButton className={classes.drawerIconContainer} onClick={() => setOpenDrawer(!openDrawer)} disableRipple>
        <MenuIcon className={classes.drawerIcon} />
      </IconButton>
    </React.Fragment>
  );

  return (
    <>
      <ElevationScroll>
        <AppBar position="fixed" color="primary">
          <ToolBar disableGutters>
            <Button disableRipple component={Link} to="/" className={classes.logocontainer}>
              <img src={logo} alt="company logo" className={classes.logo} />
            </Button>
            {matches ? drawer : tabs}
          </ToolBar>
        </AppBar>
      </ElevationScroll>

      <div className={classes.toolbarMargin} />
    </>
  );
}

export default Header;
