import React from "react";
import AppBar from "@mui/material/AppBar";
import ToolBar from "@mui/material/Toolbar";
import useScrollTrigger from "@mui/material/useScrollTrigger";
import { makeStyles } from "@mui/styles";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { Button } from "@mui/material";
import { useState } from "react";
import logo from "../../assets/logo.svg";

function ElevationScroll(props) {
  const { children } = props;

  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
  });

  return React.cloneElement(children, {
    elevation: trigger ? 4 : 0,
  });
}

const useStyles = makeStyles((theme) => ({
  toolbarMargin: {
    ...theme.mixins.toolbar,
    marginBottom: "3em",
  },
  logo: {
    height: "7rem",
  },
  tabContainer: {
    marginLeft: "auto",
  },
  tab: {
    ...theme.typography.tab,
    minWidth: 10,
    marginLeft: "25px",
    color: "white !important",
  },
  button: {
    ...theme.typography.estimate,
    borderRadius: "50px !important",
    marginLeft: "50px !important",
    marginRight: "25px !important",
    height: "45px !important",
  },
}));

function Header(props) {
  const classes = useStyles();
  const [value, setValue] = useState(0);
  const changeHandler = (e, value) => {
    setValue(value);
  };
  return (
    <>
      <ElevationScroll {...props}>
        <AppBar position="static">
          <Toolbar className={classes.root}>
            <img alt="compony logo " src={logo192} className={classes.logo} />

            <Typography variant="h5">Arc Development</Typography>
            <Tabs value={value} onChange={handleChange} className={classes.tabContainer} indicatorColor="primary">
              <Tab label="Home" className={classes.tab} component={Link} to="/" />
              <Tab label="Services" className={classes.tab} component={Link} to="/services" />
              <Tab label="Revolution" className={classes.tab} component={Link} to="/revolution" />
              <Tab label="About Us" className={classes.tab} component={Link} to="/about" />
              <Tab label="Contact Us" className={classes.tab} component={Link} to="/contact" />
            </Tabs>
            <Button variant="contained" color="primary" className={classes.button}>
              Search
            </Button>
          </Toolbar>
        </AppBar>
      </ElevationScroll>

      <div className={classes.toolbarMargin} />
    </>
  );
}

export default Header;
